package com.perscholas.my_first_project;

public class InnerOuterLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for (int i = 0; i < 3; i++) {
	for (int j = 0; j < 3; j++)
	{
	System.out.println("Outer loop: " + i +  " Inner loop: " + j);
	}
}
	}

}
//this is circle inner loop inside outer loop. we have all values from inner loop for outer