public class JavaFirstDoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 int k = 11;
 do {
	 if(k%2 ==0) {
		 System.out.println("k is " + k);
		 k++;
		 }
 } while (k==10);
	 }
	 
	 }

 
 
		
		
		

